Kadane Algorithm.code:
#include <bits/stdc++.h>
using namespace std;
long long kadane(vector<int>& a){ long long best=-1e18, cur=0;
 for(int x:a){ cur=max((long long)x,cur+x); best=max(best,cur);} return best; }
int main(){ vector<int>a={-2,1,-3,4}; cout<<kadane(a);}

