#include <bits/stdc++.h>
using namespace std;
void bfs(int start, vector<vector<int>>& adj){ queue<int> q; q.push(start);
 vector<int> vis(adj.size()); vis[start]=1;
 while(!q.empty()){ int u=q.front(); q.pop();
 for(int v:adj[u]) if(!vis[v]) vis[v]=1, q.push(v); }}
int main(){ vector<vector<int>> g={{1},{0,2},{1}}; bfs(0,g);}

