Segment Tree (Range Sum).code:
#include <bits/stdc++.h>
using namespace std;
struct SegTree{ int n; vector<long long> tree;
 SegTree(vector<long long>& a){ n=a.size(); tree.assign(4*n,0); build(a,1,0,n-1);} 
 long long build(vector<long long>&a,int idx,int l,int r){ if(l==r) return tree[idx]=a[l];
 int m=(l+r)/2; return tree[idx]=build(a,idx*2,l,m)+build(a,idx*2+1,m+1,r);} };
int main(){ vector<long long>a={1,2,3}; SegTree t(a);}
